mapper.update:
UPDATE tb_festival
fes_name=#{fesName}, m_id=#{member.mId}, fes_photo=#{fesPhoto}, sights_num=#{sights.sightsNum}, fes_start_date=#{fesStartDate}, fes_end_date=#{fesEndDate}, fes_detail=#{fesDetail}, fes_web=#{fesWeb}, fes_reg_date=#{now()}, fes_state=#{fesState}
fes_num=#{fesNum}

mapper.insert:



	SELECT
		(case COUNT(*)
		when 0 then 'fes_1'
		ELSE CONCAT('fes_', (MAX(CAST(substring(fes_num,5) AS DECIMAL))+1))
		END) AS fesNum
	FROM
		tb_festival;

	INSERT INTO tb_festival
	(fes_num, sights_num, m_id, fes_name, fes_photo, fes_start_date, fes_end_date, fes_detail, fes_web, fes_reg_date, fes_state)			
	
	VALUES ( #{fesNum}	
			,#{sights.sightsNum}
			,#{member.mId}
			,#{fesName}
			,#{fesPhoto}
			,#{fesStartDate}
			,#{fesEndDate}
			,#{fesDetail}
			,#{fesWeb}
			,now()
			,#{fesState}
			);
